class Queue:
    def __init__(self):
        self.items = []
        self.full = False

    def is_full(self):
        return self.full

    def enqueue(self, item):
        if not self.is_full():
            self.items.append(item)
        else:
            print('The queue is full.')

    def dequeue(self):
        if not self.is_full():
            return self.items.pop()
        else:
            print('The queue is full.')

    def peek(self):
        if not self.is_full():
            return self.items[-1]
        else:
            print('The queue is full.')

    def __len__(self):
        return len(self.items)

