class BanBot extends Bot {
    constructor(client) {
        super(client, {
            name: 'BanBot',
            description: 'A Discord bot that bans users.',
            commands: [
                {
                    name: 'ban',
                    description: 'Bans a user.',
                    usage: 'BanBot ban [user]',
                    minPermissions: 0,
                    maxPermissions: 0,
                    type: 'slash',
                    options: [
                        {
                            name: 'reason',
                            description: 'Reason for banning the user.',
                            type: 'string',
                            minLength: 1,
                            maxLength: 100,
                            required: true
                        }
                    ]
                }
            ]
        });
    }

    async ban(message, args) {
        const reason = args.slice(1).join(' ');
        const user = message.mentions.users.first();
        if (!user) {
            message.reply('You must mention a user to ban them.');
            return;
        }
        await user.ban(reason);
        message.reply('The user \'' + user.tag + '\' has been banned.');
    }
}

