class Queue:
    def __init__(self):
        self.items = []
        self.full = False

    def is_full(self):
        return self.full

    def enqueue(self, item):
        if not self.is_full():
            self.items.append(item)
            self.full = True

    def dequeue(self):
        if not self.is_full():
            return self.items.pop()

    def size(self):
        return len(self.items)
