def lcs(s1, s2): 
    n1 = len(s1) 
    n2 = len(s2) 

    # matrix to store the dp values 
    dp = [[None]*(n2+1) for _ in range(n1+1)] 

    # initialize the dp values 
    for i in range(n1+1): 
        for j in range(n2+1): 
            if i == 0 or j == 0 : 
                dp[i][j] = 0
            elif s1[i-1] == s2[j-1]: 
                dp[i][j] = dp[i-1][j-1] + 1
            else: 
                dp[i][j] = max(dp[i-1][j] , dp[i][j-1]) + 1

    # print the dp values 
    for i in range(n1+1): 
        for j in range(n2+1): 
            print(dp[i][j])

    # return the longest subsequence 
    return dp[n1-1][n2-1]

