def binary_search(arr, target):
    left = 0
    right = len(arr)-1
    while left <= right:
        mid = (left+right) // 2
        if arr[mid] == target:
            return mid
        elif target < arr[mid]:
            right = mid - 1
        else:
            left = mid + 1

    return -1

class Queue:
    def __init__(self):
        self.items = []
        self.full = False

    def is_full(self):
        return self.full

    def enqueue(self, item):
        if not self.is_full():
            self.items.append(item)
            self.full = True
        else:
            print("The queue is full")

    def dequeue(self):
        if not self.is_full():
            return
        else:
            self.full = False
        return self.items.pop()
