// Function to read a text file line by line
void readTextFile(std::string filename, std::vector<std::string> &lines) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Error: Could not open file " << filename << std::endl;
        return;
    }
    std::string line;
    std::getline(file, line); // Read the first line
    while (std::getline(file, line)) {
        lines.push_back(line);
    }
}

// Function to count the occurrences of a word in a text file
int countOccurrences(std::string filename, std::string word) {
    std::vector<std::string> lines;
    readTextFile(filename, lines);
    int count = 0;
    for (const std::string& line : lines) {
        if (line.find(word) != std::string::npos) {
            count++;
        }
    }
    return count;
}

