def fibonacci(n):
    """
    Returns the nth Fibonacci number
    """
    if n < 0:
        return None
    elif n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        return fibonacci(n - 1) + fibonacci(n - 2)

def fibonacci_dp(n):
    """
    Returns the nth Fibonacci number using dynamic programming
    """
    if n < 0:
        return None
    elif n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        if n < 4:
            return fibonacci(n - 1) + fibonacci(n - 2)
        else:
            return fibonacci(n - 1) + fibonacci(n - 2) + fibonacci(n - 3)

