void convertMarkdownToHtml(std::string& markdown, std::string& html)
{
    // convert markdown to html
    markdown = Markdown::toHtml(markdown);
    // replace all tabs with spaces
    markdown = markdown.replace("\t", " ");
    // replace all double quotes with "&quot;
    markdown = markdown.replace("""", "&quot;");
    // replace all single quotes with '&apos;
    markdown = markdown.replace("'", "&apos;");
    // replace all entities with their characters
    markdown = Entities::toChars(markdown);
    // return the html string
    html = markdown;
}

