class Queue:
    def __init__(self):
        self.front = None
        self.back = None

    def enqueue(self, item):
        if self.front is None:
            self.front = item
        else:
            self.back = item

    def dequeue(self):
        if self.front is not None:
            return self.front
        else:
            return self.back

    def is_empty(self):
        if self.front is None and self.back is None:
            return True
        else:
            return False

