    // Add closing tags for headings
    for (int i = 1; i <= 6; ++i) {
        std::regex closeHeadingRegex("<h" + std::to_string(i) + ">");
        html = std::regex_replace(html, closeHeadingRegex, "</h" + std::to_string(i) + ">");
    }

    return html;
}

int main() {
