#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int main() {
    std::string filename = "yourfile.txt"; // Replace with your file path
    std::string targetWord = "targetword"; // Replace with the word you want to count
    std::string word;
    int count = 0;

    // Open the file
    std::ifstream file(filename);
    if (!file.is_open()) {
        std::cerr << "Could not open the file - '" << filename << "'" << std::endl;
        return EXIT_FAILURE;
    }

    // Read the file line by line
    std::string line;
    while (getline(file, line)) {
        // Use a string stream to read each word
        std::istringstream iss(line);
        while (iss >> word) {
            // Increment count if the word matches the target word
            if (word == targetWord) {
                count++;
            }
        }
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word '" << targetWord << "' occurred " << count << " times in the file." << std::endl;

    return 0;
