# First-person controller script for Godot Engine 3.x

# Set up the camera and player node
var camera : Camera2D = get_node(type = "Camera2D")
var player : KinematicBody = get_node(type = "KinematicBody")

# Set up the movement and rotation controls
var horizontal_speed = 10.0
var vertical_speed = 5.0
var turn_speed = 0.1

# Set up the look_at function
func look_at(target : Node2D) -> void {
    # Calculate the direction from the player to the target
    var direction = (target.position - player.position).normalized()

    # Set the camera's rotation to face the target
    camera.rotation = atan2(direction.y, direction.x) * 180 / PI
}

# Set up the movement functions
func move_forward() -> void {
    # Calculate the forward direction
    var forward = (player.position + Vector2(0, vertical_speed * sin(camera.rotation))).normalized()

    # Move the player forward
    player.position += forward * horizontal_speed * delta * Time.delta
}

func move_backward() -> void {
    # Calculate the backward direction
    var backward = (player.position - Vector2(0, vertical_speed * sin(camera.rotation))).normalized()

    # Move the player backward
    player.position += backward * horizontal_speed * delta * Time.delta
}

