#include <SDL_mixer.h>

// Struct to represent the snake
struct Snake {
    int x, y; // Position
    int length; // Length of the snake
    SDL_Texture* texture; // Image for the snake
};

// Struct to represent the food
struct Food {
    int x, y; // Position
    SDL_Texture* texture; // Image for the food
};

// Struct to represent the game state
struct GameState {
    Snake snake; // Snake object
    Food food; // Food object
    bool game_over; // Game over flag
};

// Function to initialize the game state
GameState init_game() {
    // Load the images for the snake and food
    SDL_Texture* snake_texture = SDL_CreateTextureFromSurface(SDL_LoadBMP("snake.bmp"));
    SDL_Texture* food_texture = SDL_CreateTextureFromSurface(SDL_LoadBMP("food.bmp"));

    // Create the snake and food objects
    Snake snake = {0, 0, 10, snake_texture};
    Food food = {100, 100, food_texture};

    // Initialize the game state
    GameState state = {snake, food, false};

    return state;
}

// Function to update the game state
void update_game(GameState* state) {
    // Update the snake's position
    state->snake.x += 10;

    // Check for collision with the walls or itself
    if (state->snake.x < 0 || state->snake.x >= 800 || state->snake.y < 0 || state->snake.y >= 600) {
        // Handle collision
        state->game_over = true;
    }

    // Check for food consumption
    if (state->snake.x == state->food.x && state->snake.y == state->food.y) {
        // Update the snake's length
        state->snake.length += 10;

        // Update the food's position
        state->food.x = rand() % 800;
        state->food.y = rand() % 600;
    }
}

// Function to draw the game objects on the screen
void draw_game(GameState* state) {
    // Clear the screen
    SDL_RenderClear(SDL_GetRenderTarget());

    // Draw the snake
    SDL_RenderCopy(SDL_GetRenderTarget(), state->snake.texture, NULL, NULL);

    // Draw the food
    SDL_RenderCopy(SDL_GetRenderTarget(), state->food.texture, NULL, NULL);

    // Update the screen
    SDL_RenderPresent(SDL_GetRenderTarget());
}

// Main function
int main() {
    // Initialize SDL
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL_Init() failed: %s\n", SDL_GetError());
        return 1;
    }

    // Load the images
    SDL_Texture* snake_texture = SDL_CreateTextureFromSurface(SDL_LoadBMP("snake.bmp"));
    SDL_Texture* food_texture = SDL_CreateTextureFromSurface(SDL_LoadBMP("food.bmp"));

    // Initialize the game state
    GameState state = init_game();

    // Set up the game loop
    while (1) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                break;
            }
        }

        // Update the game state
        update_game(&state);

        // Draw the game objects on the screen
        draw_game(&state);
    }

    // Clean up
    SDL_DestroyTexture(snake_texture);
    SDL_DestroyTexture(food_texture);
    SDL_Quit();

    return 0;
}
