```
#include <fstream>
#include <string>

int main() {
  // Open the file in read mode
  std::ifstream file("file.txt");

  // Count the number of occurrences of the word "word" in the file
  int wordCount = 0;
  while (file.good()) {
    std::string line = file.read_line();
    if (line.find("word") != std::string::npos) {
      wordCount++;
    }
  }

  // Print the number of occurrences of the word
  std::cout << "The word 'word' occurs " << wordCount << " times in the file." << std::endl;
  // Close the file
  file.close();

  return 0;
}

