function checkPrime(arr) {
  for (let i = 0; i < arr.length; i++) {
    let num = arr[i];
    if (isPrime(num)) {
      console.log(`${num} is prime`);
      return;
    }
  }
  console.log(`No prime numbers found in the array`);
}

function isPrime(num) {
  if (num < 2) return false;
  for (let i = 2; i < num; i++) {
    if (num % i === 0) return false;
  }
  return true;
}
```
