#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

int main() {
    std::string input_file = "input.txt"; // Replace with your input file name
    std::string search_word = "word"; // Replace with the word you want to search for
    int word_count = 0;

    std::ifstream file(input_file);
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << input_file << std::endl;
        return 1;
    }

    std::string line;
    while (std::getline(file, line)) {
        std::istringstream iss(line);
        std::string word;

        while (iss >> word) {
            if (word == search_word) {
                ++word_count;
            }
        }
    }

